insert into i18n (page,lang,category,id,stage,text) values
('discovery/about.tmpl','en','tparm','lang',null,'en'),
('discovery/about.tmpl','en','tparm','pagetitle',null,'About fILL'),
('discovery/about.tmpl','en','js_lang_data','image-attribution',null,'Image care of '),
('discovery/about.tmpl','en','js_lang_data','about-heading',null,'About'),
('discovery/about.tmpl','en','js_lang_data','about-p1',null,'fILL is the interlibrary loan management system for all Manitoba public libraries. It connects your local library to the millions of items available in Manitoba''s libraries.'),
('discovery/about.tmpl','en','js_lang_data','about-p2',null,'You are currently in the discovery-only fILL site. This will let you search all of the public libraries in Manitoba, but does not let you make any interlibrary loan requests.'),
('discovery/about.tmpl','en','js_lang_data','about-p3',null,'If you are a patron of a Manitoba public library, and your library supports SIP2 authentication, you can log in to fILL by clicking on the orange "fILL" logo in the upper left corner of the page. Logging in will enable you to easily ask your library to request titles you have discovered.'),
('discovery/about.tmpl','en','js_lang_data','about-privacy',null,'Privacy'),
('discovery/about.tmpl','en','js_lang_data','about-privacy-p1',null,'fILL does not keep a record of your search history nor  does it keep a record of request transactions after borrowed items are returned. Logging into your account is necessary in order for your library to validate the request; some non-identifying data may be retained for statistical purposes.');
