insert into i18n (page,lang,category,id,stage,text) values
('public/new.tmpl','en','tparm','lang',null,'en'),
('public/new.tmpl','en','tparm','pagetitle',null,'New to fILL'),
('public/new.tmpl','en','js_lang_data','image-attribution',null,'Image care of'),
('public/new.tmpl','en','js_lang_data','menu_login',null,'Welcome'),
('public/new.tmpl','en','js_lang_data','menu_new',null,'New to fILL?'),
('public/new.tmpl','en','js_lang_data','about-heading',null,'About fILL'),
('public/new.tmpl','en','js_lang_data','about-p1',null,'fILL is the interlibrary loan management system for all Manitoba public libraries.  It connects your local library to the millions of items available in Manitoba''s libraries.'),
('public/new.tmpl','en','js_lang_data','about-p2',null,'If you are unable to find an item after searching your library''s catalogue you can use fILL to find it online and have it delivered to your public library.'),
('public/new.tmpl','en','js_lang_data','about-p3',null,'fILL enables library patrons to quickly and easily search the catalogues of many Manitoba public libraries at the same time. When you request an item through fILL your local library staff will try to borrow the item from the lending library for you.'),
('public/new.tmpl','en','js_lang_data','login-heading',null,'Logging in to fILL'),
('public/new.tmpl','en','js_lang_data','login-p1',null,'To use fILL you will need a valid Manitoba public library card.'),
('public/new.tmpl','en','js_lang_data','login-p2-span1',null,'The first time you log in, you will need to:'),
('public/new.tmpl','en','js_lang_data','login-list-item1',null,'Choose the region of the Province where you live. This will show you a list of cities and towns in that region which have public libraries.'),
('public/new.tmpl','en','js_lang_data','login-list-item2',null,'Select the location where you are a library member.'),
('public/new.tmpl','en','js_lang_data','login-p2-span2',null,'fILL will remember this (by storing a cookie on your computer), so you won''t need to do it again.'),
('public/new.tmpl','en','js_lang_data','login-p3',null,'Enter your library card number and PIN # (exactly as you would for logging in to your library''s web site, or eLibraries Manitoba)'),
('public/new.tmpl','en','js_lang_data','privacy-heading',null,'Privacy'),
('public/new.tmpl','en','js_lang_data','privacy-p1',null,'fILL does not keep a record of your search history nor does it keep a record of request transactions after borrowed items are returned. Logging in is necessary in order for your library to validate the request; some non-identifying data may be retained for statistical purposes.')
insert into i18n (page,lang,category,id,stage,text) values
('public/new.tmpl','fr','tparm','lang',null,'fr'),
('public/new.tmpl','fr','tparm','pagetitle',null,'Au sujet de fILL'),
('public/new.tmpl','fr','js_lang_data','image-attribution',null,'Image de'),
('public/new.tmpl','fr','js_lang_data','menu_login',null,'Bienvenu'),
('public/new.tmpl','fr','js_lang_data','menu_new',null,'Vous êtes nouveau à fILL?'),
('public/new.tmpl','fr','js_lang_data','about-heading',null,'Au sujet de fILL'),
('public/new.tmpl','fr','js_lang_data','about-p1',null,'fILL est le système de gestion des prêts interbibliothèques utilisé pour toutes les bibliothèques publiques du Manitoba. Il relie votre bibliothèque locale aux millions d’articles accessibles dans les bibliothèques du Manitoba.'),
('public/new.tmpl','fr','js_lang_data','about-p2',null,'Si vous ne trouvez pas un article après avoir fait une recherche dans le catalogue de votre bibliothèque, vous pouvez utiliser fILL pour trouver l’article en ligne et le faire livrer à votre bibliothèque publique.'),
('public/new.tmpl','fr','js_lang_data','about-p3',null,'fILL permet aux usagers des bibliothèques de consulter rapidement et facilement les catalogues d’un grand nombre de bibliothèques publiques du Manitoba simultanément. Lorsque vous demandez un article par le truchement de fILL, le personnel de votre bibliothèque locale tente d’emprunter l’article à la bibliothèque de prêt en votre nom.'),
('public/new.tmpl','fr','js_lang_data','login-heading',null,'Connexion à fILL'),
('public/new.tmpl','fr','js_lang_data','login-p1',null,'Pour utiliser fILL, vous devez posséder une carte de bibliothèque publique du Manitoba valide.'),
('public/new.tmpl','fr','js_lang_data','login-p2-span1',null,'La première fois que vous vous connecterez, vous devrez:'),
('public/new.tmpl','fr','js_lang_data','login-list-item1',null,'choisir la région de la province dans laquelle vous habitez. Le système affichera une liste des villes de cette région qui comptent des bibliothèques publiques;'),
('public/new.tmpl','fr','js_lang_data','login-list-item2',null,'choisir le lieu où vous êtes abonné à une bibliothèque.'),
('public/new.tmpl','fr','js_lang_data','login-p2-span2',null,'fILL mémorisera ces renseignements (en stockant un témoin sur votre ordinateur). Vous n’aurez donc plus à entrer ces renseignements.'),
('public/new.tmpl','fr','js_lang_data','login-p3',null,'Entrez votre numéro de carte de bibliothèque et votre NIP (exactement de la même façon dont vous vous connectez au site Web de votre bibliothèque ou à eLibraries Manitoba).'),
('public/new.tmpl','fr','js_lang_data','privacy-heading',null,'Protection des renseignements personnels'),
('public/new.tmpl','fr','js_lang_data','privacy-p1',null,'fILL ne conserve pas de dossier de votre  historique de recherche ni des opérations demandées après que les articles empruntés sont retournés. La connexion est nécessaire pour permettre à votre bibliothèque de valider la demande. Certaines données non identificatoires peuvent être conservées à des fins statistiques.')
